// Intercept the registration request
cy.intercept('POST', '/users', (req) => {
    req.reply({
        statusCode: 201,
        body: {
            id: '123',
            username: testUser.username
        }
    });
}).as('registerUser');
cy.dataCy('submit').click();
cy.wait('@registerUser');
