// Intercept with error response
cy.intercept('POST', '/users', (req) => {
    req.reply({
        statusCode: 400,
        body: { message: 'Username already exists' }
    });
}).as('registerError');
cy.dataCy('submit').click();
cy.wait('@registerError');
cy.contains('Username already exists');
cy.url().should('include', '/register');

// Intercept with delay
cy.intercept('POST', '/users', (req) => {
    setTimeout(() => {
        req.reply({
            statusCode: 201,
            body: {
                id: '123',
                username: testUser.username
            }
        });
    }, 1000);
}).as('registerDelay');
cy.dataCy('submit').click();
// Check disabled states
['username', 'password', 'confirm-password', 'submit', 'back-to-login'].forEach(field => {
    cy.dataCy(field).should('be.disabled');
});
cy.wait('@registerDelay');
cy.url().should('include', '/home');
